import React, { Component } from "react";
import axios from "axios";
import "./imageGallery.css";
import Topbanner from "./topBanner";
import Navbar from "./navbar";
import Footer from "./footer";
import img1 from "./pics/course_img1.jpg";
import img2 from "./pics/resized_group.png";
import img3 from "./pics/course_img4.jpg";

class ImageGallery extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courseImages: [],
    };
  }
  async componentDidMount() {
    const { data: courseImages } = await axios.get(
      "http://127.0.0.1:8000/courseImages/"
    );

    this.setState({ courseImages });
    console.log(courseImages);
  }
  // getImages(image) {
  //   console.log(image);
  //   return (
  //     <div class="row">
  //       <div class="col-md-4">
  //         <div class="thumbnail">
  //           <a href="#">
  //             <img src={image} alt="img" />
  //           </a>
  //         </div>
  //       </div>
  //     </div>
  //   );
  // }

  render() {
    const { courseImages } = this.state;
    // let imagesArr = [];
    // if (courseImages.length > 0) {
    //   for (let i = 0; i < courseImages.length; i++) {
    //     imagesArr.push(this.getImages(courseImages[i]));
    //   }
    // }
    // console.log(imagesArr);
    return (
      <div className="maindiv">
        <Topbanner />
        <Navbar />
        <br />
        {/* <div className="container imagegallery">{imagesArr}</div> */}
        <div className="container imagegallery">
          <div class="row">
            <div className="col-md-4">
              {/* <div className="thumbnail"> */}
              {/* <a href="#"> */}
              <img
                className="img-fluid img-thumbnail"
                src={img1}
                alt="Lights"
              />
              {/* </a> */}
              {/* </div> */}
            </div>
            <div className="col-md-4">
              {/* <div class="thumbnail"> */}
              {/* <a href="#"> */}
              <img
                className="img-fluid img-thumbnail"
                src={img2}
                alt="Nature"
              />
              {/* </a> */}
              {/* </div> */}
            </div>
            <div className="col-md-4">
              {/* <div class="thumbnail"> */}
              {/* <a href="#"> */}
              <img
                className="img-fluid img-thumbnail"
                src={img3}
                alt="Nature"
              />
              {/* </a> */}
              {/* </div> */}
            </div>
          </div>
        </div>

        <br />
        <Footer />
      </div>
    );
  }
}

export default ImageGallery;

