import React, { Component } from "react";
import welcome from "./pics/welcome.jpg";
import "./welcome.css";
class Welcome extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <div class="row ar1">
        <div class="col-sm-6">
          <h3 className="h3font">Welcome to SSI</h3>
          <p>
            {/* Delivering best quality Training and provide Consultancy
                    services in Quality, Health, Safety & Environment. */}
            SafeSolutions International (SSI) has a picture of two Key Players
            behind it. It has grown from two-person organization as freelancers
            in association with several other training providers and experts,
            offering a wide range of skills,expertise,awareness and train the
            trainer courses. <br />
            SSI team has a strong commitment towards quality and confidentiality
            which we prove while helping organizations to align their QHSE
            systems and practices. <br />
            Our trainings enhance the employeesâ€™ knowledge & awareness to
            perform their job and everyday work confidently with high quality
            and safety.
          </p>
        </div>
        <div class="col-sm-6 text-center">
          <img
            src={welcome}
            class="img-fluid max-width: 100% height: auto"
            alt="welcome img"
          />
        </div>
      </div>
    );
  }
}

export default Welcome;
