import React, { Component } from "react";
import axios from "axios";
import "./imageGallery.css";
import Topbanner from "./topBanner";
import Navbar from "./navbar";
import Footer from "./footer";

class ImageGallery extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courseImages: [],
    };
  }
  async componentDidMount() {
    const { data: courseImages } = await axios.get(
      "http://127.0.0.1:8000/courseImages/"
    );

    this.setState({ courseImages });
    console.log(courseImages);
  }
  getImages(image) {
    console.log(image);
    return (
      <div class="row">
        <div class="col-md-4">
          <div class="thumbnail">
            <a href="#">
              <img src={image} alt="Lights" style="width:100%" />
            </a>
          </div>
        </div>
      </div>
    );
  }

  render() {
    let imagesArr = [];
    const { courseImages } = this.state;

    // <div className="container imagegallery">
    //       {courseImages.map((image) => {
    //         return (
    //           <div class="thumbnail">
    //             <a href="#">
    //               <img src={image.image} alt="img" />
    //               {/* <p>{image.id}</p> */}
    //             </a>
    //           </div>
    //         );
    //       })}
    //     </div>
    return (
      <div className="maindiv">
        <Topbanner />
        <Navbar />
        <div className="container imagegallery">
          <div class="row">
            <div class="col-md-4">
              <div class="thumbnail">
                <a href="/w3images/lights.jpg">
                  <img
                    src="/w3images/lights.jpg"
                    alt="Lights"
                    style="width:100%"
                  />
                  <div class="caption">
                    <p>Lorem ipsum...</p>
                  </div>
                </a>
              </div>
            </div>
            <div class="col-md-4">
              <div class="thumbnail">
                <a href="/w3images/nature.jpg">
                  <img
                    src="/w3images/nature.jpg"
                    alt="Nature"
                    style="width:100%"
                  />
                  <div class="caption">
                    <p>Lorem ipsum...</p>
                  </div>
                </a>
              </div>
            </div>
            <div class="col-md-4">
              <div class="thumbnail">
                <a href="/w3images/fjords.jpg">
                  <img
                    src="/w3images/fjords.jpg"
                    alt="Fjords"
                    style="width:100%"
                  />
                  <div class="caption">
                    <p>Lorem ipsum...</p>
                  </div>
                </a>
              </div>
            </div>
          </div>
        </div>

        <Footer />
      </div>
    );
  }
}

export default ImageGallery;
