import React, { Component } from "react";
import "./announcements.css";
class Announcements extends Component {
  constructor(props) {
    super(props);
    this.state = {
      announcements: [
        "Announcement1",
        "Announcement2",
        "Announcement3",
        "Announcement4",
        "Announcement5",
      ],
    };
  }
  render() {
    let aStyle = {
      //   paddingLeft: "0px",
      margin: "0px",
      color: "white",
    };
    return (
      <div class="brder">
        <h5 className="headcolor">Announcements</h5>
        <ul>
          {this.state.announcements.map((announcement) => (
            <a href="/" style={aStyle}>
              {" "}
              {announcement}{" "}
            </a>
          ))}
        </ul>
      </div>
    );
  }
}

export default Announcements;
