import React, { Component } from "react";
import Topbanner from "./topBanner";
import Navbar from "./navbar";
import Footer from "./footer";
import axios from "axios";
class AllCourses extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courses: [],
    };
  }
  async componentDidMount() {
    const { data: courses } = await axios.get("http://127.0.0.1:8000/courses/");
    this.setState({ courses });
  }
  getCourses(course) {
    return (
      <div>
        <a href="#" class="display: flex" className="linkcolor">
          {course.title}
        </a>
        <br />
      </div>
    );
  }
  render() {
    let CoursesArr = [];
    const courses = this.state.courses;
    if (courses.length > 0) {
      for (let i = 0; i < courses.length; i++) {
        CoursesArr.push(this.getCourses(courses[i]));
      }
    }
    return (
      <div>
        <Topbanner />
        <Navbar />
        <br />
        <div className="container-fluid">
          <h4>Here's a sneak peak of our courses</h4>
          <p>Our courses promise a rewarding career.</p>
        </div>

        <div className="container-fluid courselist">{CoursesArr}</div>
        <br />
        <br />
        <br />
        <Footer />
      </div>
    );
  }
}

export default AllCourses;
