          return (
            <div class="col-md-4">
              <img
                className="img-fluid img-thumbnail"
                src={image.image}
                alt="img"
              />
            </div>
          );
        })}

        <br />
        {/* <Footer /> */}
        <br />
        <br />
        <br />
      </div>
    );
  }
}

export default ImageGallery;
