import React, { Component } from "react";
import axios from "axios";
import "./imageGallery.css";
import Topbanner from "./topBanner";
import Navbar from "./navbar";
import Footer from "./footer";
import img1 from "./pics/free.png";
import img2 from "./pics/iosh.png";
import img3 from "./pics/diplomas.png";

class ImageGallery extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courseImages: [],
    };
  }
  async componentDidMount() {
    const { data: courseImages } = await axios.get(
      "http://127.0.0.1:8000/courseImages/"
    );

    this.setState({ courseImages });
    console.log(courseImages);
  }
  getImages(image) {
    console.log(image);
    return (
      // <div class="row">
      //   <div class="col-md-4">
      <div class="thumbnail">
        <a href="#">
          <img src={image} alt="img" />
        </a>
      </div>
      //   </div>
      // </div>
    );
  }

  render() {
    let imagesArr = [];
    const { courseImages } = this.state;
    if (courseImages.length > 0) {
      for (let i = 0; i < courseImages.length; i++) {
        imagesArr.push(this.getImages(courseImages[i]));
      }
    }
    console.log(imagesArr);
    return (
      <div className="maindiv">
        <Topbanner />
        <Navbar />
        <br />
        {/* <div className="container imagegallery">{imagesArr}</div> */}
        {courseImages.map((image) => {
          return (
            <div class="row">
              <div class="col-md-4">
                <div class="thumbnail">
                  <a href="#">
                    <img class="img-fluid" src={image.image} alt="img" />
                    {/* <p>{image.id}</p> */}
                  </a>
                </div>
              </div>
            </div>
          );
        })}

        <br />
        <Footer />
      </div>
    );
  }
}

export default ImageGallery;
