import React, { Component } from "react";
import Announcements from "./announcements";
import Events from "./upcomingEvents";
import "./footer.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { SocialIcon } from "react-social-icons";
import axios from "axios";

export default class Footer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      archives: [],
      courseImages: [],
    };
  }

  async componentDidMount() {
    const { data: courseImages } = await axios.get(
      "http://127.0.0.1:8000/courseImages/"
    );
    const { data: archives } = await axios.get(
      "http://127.0.0.1:8000/archives/"
    );
    // console.log(archives);
    this.setState({ courseImages, archives });
    // console.log(courseImages);
  }

  render() {
    // console.log(this.state.archives);
    // console.log(this.state.courseImages);
    return (
      <div class="container-fluid">
        <div
          className="row display: flex primaryBackground footerMainDiv"
          style={{ backgroundColor: "#002f5f" }}
        >
          <div className="col-md-4 aligncontent pad">
            <strong style={{ color: "#f7941d" }}>Contact Us</strong>
            <br />
            <p className="fontsize inlineDisplay">
              Pakistan : +92 334 1145908 <br />
              Kuwait : +965 65106756, +965 99614518
              <br />
              <svg
                width="1em"
                height="1em"
                viewBox="0 0 16 16"
                class="bi bi-envelope"
                fill="#f7cd00"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  fill-rule="evenodd"
                  d="M0 4a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v8a2 2 0 0 1-2 2H2a2 2 0 0 1-2-2V4zm2-1a1 1 0 0 0-1 1v.217l7 4.2 7-4.2V4a1 1 0 0 0-1-1H2zm13 2.383l-4.758 2.855L15 11.114v-5.73zm-.034 6.878L9.271 8.82 8 9.583 6.728 8.82l-5.694 3.44A1 1 0 0 0 2 13h12a1 1 0 0 0 .966-.739zM1 11.114l4.758-2.876L1 5.383v5.73z"
                />
              </svg>
              <span> admin@hsecertintl.com</span>
              <br />
              HseCertIntl, Klass wala road, Pasrur <br />
              dist. Sialkot, Pakistan
              <br />
              HseCertIntl, Mahboula, Ahmadi Kuwait.
            </p>
          </div>

          <div className="col-md-4 aligncontent pad">
            <strong style={{ color: "#f7941d" }}>Connect with us</strong>

            <br />
            <SocialIcon
              network="twitter"
              url="https://www.twitter.com"
              fgColor="white"
              bgColor="#002f5f"
              className="s_icons"
            />
            <SocialIcon
              network="facebook"
              url="https://www.facebook.com"
              fgColor="white"
              bgColor="#002f5f"
              className="s_icons"
            />
            <SocialIcon
              network="linkedin"
              url="https://www.linkedin.com"
              fgColor="white"
              bgColor="#002f5f"
              className="s_icons"
            />

            <SocialIcon
              network="instagram"
              url="https://www.instagram.com"
              fgColor="white"
              bgColor="#002f5f"
              className="s_icons"
            />
          </div>
          <div className="col-md-4 aligncontent pad">
            <strong style={{ color: "#f7941d" }}>Quick Links</strong>

            <br />
            <a href="/archives" className="footerLink fontsize">
              Archives
            </a>
            <br />
            <a href="/imageGallery" className="footerLink fontsize">
              Photo Gallery
            </a>
            <br />
            <a href="/" className="footerLink fontsize">
              Help Center(FAQ)
            </a>
            <br />

            <br />
          </div>
        </div>
      </div>
    );
  }
}
