import React, { Component } from "react";
import Announcements from "./announcements";
import Events from "./upcomingEvents";
import "./footer.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { SocialIcon } from "react-social-icons";
import axios from "axios";

export default class Footer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      archives: [],
      courseImages: [],
    };
  }

  async componentDidMount() {
    const { data: courseImages } = await axios.get(
      "http://127.0.0.1:8000/courseImages/"
    );
    const { data: archives } = await axios.get(
      "http://127.0.0.1:8000/archives/"
    );
    // console.log(archives);
    this.setState({ courseImages, archives });
    // console.log(courseImages);
  }

  render() {
    // console.log(this.state.archives);
    // console.log(this.state.courseImages);
    return (
      <div class="container-fluid">
        <div
          className="row display: flex primaryBackground footerMainDiv"
          style={{ backgroundColor: "#002f5f" }}
        >
          <div className="col-md-4 aligncontent pad">
            <strong style={{ color: "#f7941d" }}>Contact Us</strong>
            <br />
            <p className="fontsize inlineDisplay">
              Pakistan : +92 334 1145908 <br />
              Kuwait : +965 65106756, +965 99614518
              <br />
              admin@hsecertintl.com
              <br />
              HseCertIntl, Klass wala road, Pasrur <br />
              dist. Sialkot, Pakistan
              <br />
              HseCertIntl, Mahboula, Ahmadi Kuwait.
            </p>
          </div>

          <div className="col-md-4 aligncontent pad">
            <strong style={{ color: "#f7941d" }}>Connect with us</strong>

            <br />
            <SocialIcon
              network="twitter"
              url="https://www.twitter.com"
              fgColor="white"
              bgColor="#002f5f"
              className="s_icons"
            />
            <SocialIcon
              network="facebook"
              url="https://www.facebook.com"
              fgColor="white"
              bgColor="#002f5f"
              className="s_icons"
            />
            <SocialIcon
              network="linkedin"
              url="https://www.linkedin.com"
              fgColor="white"
              bgColor="#002f5f"
              className="s_icons"
            />

            <SocialIcon
              network="instagram"
              url="https://www.instagram.com"
              fgColor="white"
              bgColor="#002f5f"
              className="s_icons"
            />
          </div>
          <div className="col-md-4 aligncontent pad">
            <strong style={{ color: "#f7941d" }}>Quick Links</strong>

            <br />
            <a href="/" className="footerLink fontsize">
              Archives
            </a>
            <br />
            <a href="/imageGallery" className="footerLink fontsize">
              Photo Gallery
            </a>
            <br />
            <a href="/" className="footerLink fontsize">
              Help Center(FAQ)
            </a>
            <br />

            <br />
          </div>
        </div>
      </div>
    );
  }
}
