import React, { Component } from "react";
import axios from "axios";
import "./imageGallery.css";
import Topbanner from "./topBanner";
import Navbar from "./navbar";
import Footer from "./footer";

class ImageGallery extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courseImages: [],
    };
  }
  async componentDidMount() {
    const { data: courseImages } = await axios.get(
      "http://127.0.0.1:8000/courseImages/"
    );

    this.setState({ courseImages });
    console.log(courseImages);
  }
  getImages(image) {
    console.log(image);
    return (
      <p>hi images</p>
      //   <div class="row">
      //     <div class="col-md-4">
      //       <div class="thumbnail">
      //         <a href="#">
      //           <img src={image.title} alt="Lights" style="width:100%" />
      //         </a>
      //       </div>
      //     </div>
      //   </div>
    );
  }

  render() {
    let imagesArr = [];
    const courseImages = this.state.courseImages;
    if (courseImages.length > 0) {
      for (let i = 0; i < courseImages.length; i++) {
        imagesArr.push(this.getImages(imagesArr[i]));
      }
    }
    return (
      <div className="maindiv">
        <Topbanner />
        <Navbar />
        <div className="container">{imagesArr}</div>
        <Footer />
      </div>
    );
  }
}

export default ImageGallery;
