import React, { Component } from "react";
import welcome from "./pics/welcome.jpg";
import "./welcome.css";
class Welcome extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <div class="row ar1">
        <div class="col-sm-8">
          <h2>Welcome to Safe Solutions International</h2>
          <p>
            {/* Delivering best quality Training and provide Consultancy
                    services in Quality, Health, Safety & Environment. */}
            SafeSolutions International (SSI) has a picture of two Key Players
            behind it. It has grown from two-person organization as freelancers
            in association with several training providers and experts, offering
            a wide range of skills and expertise in Quality, Health, Safety and
            Environmental Management Systems along with Consultancy and a wide
            range of other construction safety certified trainings, awareness
            and train the trainer courses. SSI team has a strong commitment
            towards quality and confidentiality which we prove while helping
            organizations to align their QHSE systems and practices. Our
            trainings enhance the employeesâ€™ knowledge & awareness to perform
            their job and everyday work confidently with high quality and
            safety.
          </p>
        </div>
        <div class="col-sm-4">
          <img src={welcome} class="img-fluid" alt="welcome img" />
        </div>
      </div>
    );
  }
}

export default Welcome;
