  }
  render() {
    return (
      <div class="container">
        <div class="row ar1">
          <div class="col-sm-6">
            <h2>SSI - Safe Solutions International</h2>
            <p>
              Delivering best quality Training and provide Consultancy services
              in Quality, Health, Safety & Environment.
            </p>
          </div>
          <div class="col-sm-6">
            <img src={ssi} alt="ssi img" />
          </div>
        </div>

        <div class="row ar1">
          <div class="col-sm-6">
            <h2>Our Mission</h2>
            <p>
              SSI team has a strong commitment towards quality and
              confidentiality which we prove while helping organizations to
              align their QHSE systems and practices. Our trainings enhance the
              employeesâ€™ knowledge & awareness to perform their job and everyday
              work confidently with high quality and safety.
            </p>
          </div>
          <div class="col-sm-6">
            <img src={ssi} alt="ssi img" />
          </div>
        </div>
      </div>
    );
  }
}

export default Body;
