import React, { Component } from "react";
import "./body.css";
import ssi from "./pics/ssi.jpg";
import leaf from "./pics/idea1.png";
import banner1 from "./pics/banner-1.png";
import banner2 from "./pics/banner-2.jpg";
import eqhse1 from "./pics/images.jpg";

class Body extends Component {
  constructor(props) {
    super(props);
    this.state = {
      announcements: [
        "announcement1",
        "announcement2",
        "announcement3",
        "announcement4",
        "announcement5",
      ],
      trainings: [
        "Construction Safety",
        "Construction Supervisor",
        "Basic Firefighting Training",
        "IOSH Managing Safely Certification",
        "Certified scaffolders training",
      ],
    };
  }
  render() {
    return (
      <div className="container containerboundry">
        <div class="row mainRow">
          <div class="col-sm-9 contentBar">
            <div class="row" className="howWeWrk">
              <h3>How we work with you</h3>
              <p>Simply.Collaboratively.Endlessly</p>
              <div class="row container" className="any">
                <div class="col-sm-4">
                  <h>We get to know your business</h>
                  <p>
                    One of our consultants will visit you to understand your
                    business'challenges and needs.Whenever it's Health & Safety
                    or HR & Employment Law you need support with,we're the
                    experts.
                  </p>
                </div>
                <div class="col-sm-4">
                  <h>We get you back on track</h>
                  <p>
                    We'll get you up-to-date with the latest legislation by
                    reviewing or creating all your essential
                    contracts,handbooks,systems and policies.
                  </p>
                </div>
                <div class="col-sm-4">
                  <h>We're here for you 24/7</h>
                  <p>
                    Whether you need expert advice,help during a crisis or a
                    friendly ear,our industry leading experts are here for you
                    24/7.We'll also give you access to 100s of fact sheets and
                    online tools,too.
                  </p>
                </div>
              </div>
            </div>
          </div>

          <div class="col-sm-3 sideBar">
            <div>
              <h5>Announcements</h5>
              <ul>
                {this.state.announcements.map((announcement) => (
                  <a href="/"> {announcement} </a>
                ))}
              </ul>
            </div>

            <div>
              <h5>Upcoming Trainings</h5>
              <ul>
                {this.state.trainings.map((training) => (
                  <a href="/"> {training} </a>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Body;
